import React, { useEffect, useState } from 'react';
import service1 from './service1.js';
import Service from './Service.js';

function Todoo() {
  const [newTodo, setNewTodo] = useState("");
  const [todos, setTodos] = useState([]);
  const [ok, setOk] = useState(false);

  //שליפת המשימות ע"פ מ.ז
  async function getTodos() {
    if(Service.getLoginUser()){
      const to = await service1.byId(Service.getLoginUser().id)
      console.log(Service.getLoginUser().id);
      await setTodos(to.data);
    }
  }
//יצירת משימה
  async function createTodo(e) {
    e.preventDefault();    
    await service1.addTask(newTodo);
    setNewTodo("");
    await getTodos();
  }
  //עדכון משימה
  async function updateCompleted(todo, isComplete) {
    await service1.setCompleted(todo.id, isComplete);
    await getTodos();
    }
//מחיקת משימה
  async function deleteTodo(id) {
    await service1.deleteTask(id);

    await getTodos();
  }
//בתחילה שולף את כל המשימות
  useEffect(() => {
    if(Service.getLoginUser()){
       setOk(true)
      getTodos();      
    }
  }, []);
// אם המשתמש מחובר הוא מציג לו רשימות אחרת מציג לו הודעה
  return <>
{ok&&<div>
    <section className="todoapp">

      <header>
        
        <h1 style={{marginBottom:'100px'}}>todos</h1>
        
       
      </header>
      <section className="main" style={{ display: "block" }}>
        <ul className="todo-list">
        {todos.map(todo => {
            return (
              <li className={todo.isComplete ? "completed" : ""} key={todo.id}>
                <div className="view">
                  <input className="toggle" type="checkbox" defaultChecked={todo.isComplete} onChange={(e) => updateCompleted(todo, e.target.checked)} />
                  <label>{todo.name}</label>
                  <button className="destroy" onClick={() => deleteTodo(todo.id)}></button>
                </div>
              </li>
            );
          })}

 </ul>

      </section>
    </section >
  </div>} 
      {!ok &&
      <div className="todoapp header new-todo">
        <p>אתה לא מחובר - להצגת המשימות הרשם כעת</p>
        </div>}
  </>
          
}

export default Todoo;
