import axios from 'axios';
import swal from 'sweetalert';
import Service from './Service';
import { useState } from 'react';

const [user,setUser]=useState({})

//דפולטיבית API הגדרת כתובת 
axios.defaults.baseURL = "http://localhost:5162/"
//תפיסת שגיאות
axios.interceptors.response.use(function (response) {
  return response;
}, function (error) {
  swal(`${error.name}`,`${error.message}`, "error")
  console.log( + ": " + error.message);
});

//API בקשות 
export default {
  getUser:()=>{
    return user;
  },
  getTasks: async () => {
    const result = await axios.get()
    
      return result.data;
  }
  ,
  getTUsers: async () => {
    const result = await axios.get('users');    
      return result.data;
  },
  addTask: async (name) => {
    console.log('addTask', name)
    const result = axios.post(`?name=${name}`)

      return await result.data;
  }

  ,
  //-----------------------הוספת משתמש------------------
  addUser: async (user) => {
    const result = axios.post(`http://localhost:5162/addUser`,user)    
      return (await result).data;
  },
  login: async (user) => {

    const res = await axios.post("login", {user});
    console.log(res);
    setUser(res.data.myUser);
    Service.saveToken(res.data.jwt);
    return res;
  }
  ,  setCompleted: async (id, isComplete) => {
    console.log('setCompleted', { id, isComplete })
    const result = axios.put(`?id=${id}`)
      return (await result).data;
  },

  deleteTask: async (id) => {
    const result = axios.delete(`?id=${id}`)
    
      return (await result).data;
  }
};
